export default function LogoWall({
  logos = defaultLogos,
}: {
  logos?: LogoItem[];
}) {
  return (
    <section
      aria-label="Worked with and built for"
      className="w-full py-10 lg:py-16"
    >
      <div className="mx-auto w-full max-w-6xl px-4">
        <motion.h2
          className="text-2xl md:text-3xl font-semibold mb-8 text-center"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          Worked With and Built for
        </motion.h2>

        <div className="grid grid-cols-2 gap-3 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6">
          {logos.map((item, idx) => {
            const Wrapper: any = item.href ? "a" : "div";
            const wrapperProps = item.href
              ? { href: item.href, target: "_blank", rel: "noreferrer" }
              : {};
            return (
              <motion.div
                key={idx}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.5, delay: idx * 0.05 }}
                whileHover={{ y: -4, scale: 1.02 }}
              >
                <Wrapper
                  {...wrapperProps}
                  className="group flex h-14 items-center justify-center rounded-2xl border-white/5 bg-white/2 px-4 hover:border-gold/40 transition"
                  aria-label={item.name}
                  title={item.name}
                >
                  {item.icon ? (
                    <div className="opacity-80 group-hover:opacity-100 [&>*]:h-full [&>*]:w-auto">
                      {item.icon}
                    </div>
                  ) : item.src ? (
                    <Image
                      src={item.src}
                      alt={item.name}
                      width={120}
                      height={32}
                      className="h-6 w-auto opacity-80 group-hover:opacity-100"
                      priority={idx < 6}
                    />
                  ) : (
                    <span className="text-sm font-medium opacity-80 group-hover:opacity-100 transition-opacity">
                      {item.name}
                    </span>
                  )}
                </Wrapper>
              </motion.div>
            );
          })}
        </div>
      </div>
    </section>
  );
}

